catalog_ui_policy.script_false:
function onCondition() {
    g_form.clearValue('company_number');
    g_form.clearValue('action_group');
    g_form.clearValue('node');
    //g_form.clearValue('mobile_configuration');
    g_form.clearValue('agent_type');
    //g_form.clearValue('security_role1');
    g_form.clearValue('warehouse');

    g_form.setMandatory('company_number', false);
    g_form.setMandatory('action_group', false);
    g_form.setMandatory('node', false);
    //g_form.setMandatory('mobile_configuration', false);
    g_form.setMandatory('agent_type', false);
    //g_form.setMandatory('security_role1', false);
    g_form.setMandatory('warehouse', false);

    g_form.setDisplay('company_number', false);
    g_form.setDisplay('action_group', false);
    g_form.setDisplay('node', false);
    //g_form.setDisplay('mobile_configuration', false);
    g_form.setDisplay('agent_type', false);
    //g_form.setDisplay('security_role1', false);
    g_form.setDisplay('warehouse', false);
}

catalog_ui_policy.script_true:
