    g_form.clearValue('action_group');
    g_form.clearValue('node');
	//g_form.clearValue('mobile_configuration');
	g_form.clearValue('agent_type');
	//g_form.clearValue('security_role1');
	g_form.clearValue('warehouse');

    g_form.setMandatory('company_number', true);
	g_form.setMandatory('action_group', true);
	g_form.setMandatory('node', true);
	//g_form.setMandatory('mobile_configuration', true);
	g_form.setMandatory('agent_type', true);
    //g_form.setMandatory('security_role1', true);
    g_form.setMandatory('warehouse', true);

    g_form.setDisplay('company_number', true);
	g_form.setDisplay('action_group', true);
	g_form.setDisplay('node', true);
	//g_form.setDisplay('mobile_configuration', true);
	g_form.setDisplay('agent_type', true);
    //g_form.setDisplay('security_role1', true);
    g_form.setDisplay('warehouse', true);
}
